import {SING_UP} from "../types";

const defaultState = {
  username: "",
  password: "",
  confirmPassword: ""
};

export const registerReducer = (state = defaultState, action) => {
    if (action.type === SING_UP){
      return {
        ...state, ...action.payload
      }
    }else
      return state;
};
