import React, {Component} from "react";
import {connect} from "react-redux";
import axios from "axios";
import {RegisterForm} from "./Register-form";
import {setNewTodo} from "../../store/todo-list/actions";
import {setRegisterForm} from "../../store/register-form/actions";

class RegisterFormContainer extends Component {
    handleChange = event => this.props.setRegisterForm(event);

    // componentDidUpdate(prevProps, prevState, snapshot) {
    //
    // }

    handleSubmit = event => {
        if (event.key === 'Enter') {
            event.preventDefault();

            const newTodo = {label: this.props.form, important: false, done: false, key: Date.now()};
            if (this.props.form) this.props.setNewTodo(newTodo);

            this.props.setRegisterForm('');

            axios.post('http://localhost:5000/todo-list/add', newTodo)
                .then(res => console.log(res.data))
        }
    };

    render() {
        return <RegisterForm
            onChangeForm={this.handleChange}
            onSubmitForm={this.handleSubmit}
            label={this.props.form}
        />;
    }
}

const mapStateToProps = state => {
    return {
        form: state.TodoForm.label
    }
};

const mapDispatchToProps = {
    setNewTodo,
    setRegisterForm
};

export default connect(mapStateToProps, mapDispatchToProps)(RegisterFormContainer);
