import React, {Component} from "react";
import {RegisterForm} from "./Register-form";
import {connect} from "react-redux";
import {setNewTodo} from "../../store/todo-list/actions";
import {setRegisterForm} from "../../store/register-form/actions";

class RegisterFormContainer extends Component{
    onChangeForm = event => {
        event.preventDefault();
        this.props.setRegisterForm(event);
        console.log(event)
    };

    onSubmitForm = event => {
        // event.preventDefault();
        if (event.key === 'Enter')console.log(this.props.label);
        // this.props.setNewTodo(this.props.label)
    };

    render() {
        return <RegisterForm
            onChangeForm={this.onChangeForm}
            onSubmitForm={this.onSubmitForm}
            label={this.props.label}
        />;
    }
}

const mapStateToProps = state => {
    return{
        label: state.registerForm.label
    }
};

const mapDispatchToProps = {
    setNewTodo,
    setRegisterForm
};

export default connect(mapStateToProps, mapDispatchToProps)(RegisterFormContainer);
