import {ADD_TODO, DELETE_TODO, FETCH_TODO_LIST, UPDATE_TODO_DONE} from "../types";

export const setNewTodo = todo => {
    return{
        type: ADD_TODO,
        payload: todo
    }
};

export const deleteTodo = todo => {
    return{
        type: DELETE_TODO,
        payload: todo
    }
};

export const updateTodoDone = (todo, id) => {
    return{
        type: UPDATE_TODO_DONE,
        payload: todo,
        id: id
    }
};

export const fetchTodoList = () => {
    return async dispatch => {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts?_limit=5');
        const json = await response.json();
        dispatch({type: FETCH_TODO_LIST, payload: json})
    }
};
