import React, { Component } from 'react';
import "./modal.css";

const display = {
    display: 'block'
};
const hide = {
    display: 'none'
};
class Modal extends React.Component {

        state = {
            toggle: false
        };



    toggle = event => {
        this.setState(prevState => ({
            toggle: !prevState.toggle
        }));
    };
    render() {
        const modal = [];
        modal.push(
            <div className="modal" style={this.state.toggle ? display : hide}>
                <div className="modal-content">
                    <form className="col s12">

                            <div className="input-field col s6 ">
                                <i className="material-icons prefix">mode_edit</i>
                                <input
                                    type="text"
                                    id="icon_prefix22"
                                    // value={label}
                                    // onChange={event => onChangeForm(event.target.value)}
                                    // onKeyDown={onSubmitForm}
                                >
                                </input>
                                <label htmlFor="icon_prefix22">What you need to do</label>
                                <a className="btn" onClick={this.toggle}>Agree</a>
                            </div>

                    </form>
                    {/*<a className="btn" onClick={this.toggle}>Agree</a>*/}
                </div>

                    {/*<a className="btn" onClick={this.toggle}>Agree</a>*/}

            </div>
        );
        return (
            <span>
                <a className="btn-floating btn-large waves-effect waves-light yellow darken-4" onClick={this.toggle}>
                    <i className="material-icons">add</i></a>

                {modal}
            </span>
        );
    }
}

export default Modal;
