import {ADD_TODO, DELETE_TODO, FETCH_TODO_LIST} from "../types";

const defaultState = {
    todoList: [
        {
            label: "Drink some coffee",
            important: false,
            done: true,
            id: 1
        },
        {
            label: "Drink some milk",
            important: false,
            done: false,
            id: 2
        }
    ]
};

export const todoListReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_TODO:
            return {
                ...state, todoList: [...state.todoList, action.payload]
            };
        case DELETE_TODO:
            return {
                ...state, todoList: action.payload
            };
        case FETCH_TODO_LIST:
            return {
                ...state, todoList: action.payload
            };
        default:
            return state
    }
};
