import {takeEvery, put, call} from "redux-saga/effects";
import {FETCH_TODO_LIST, REQUEST_TODO_LIST} from "../../store/types";
import {hideLoader, showLoader} from "../loader/actions";
// import {setTodoList} from "../todo-list/actions";

export function* sagaWatcher() {
    yield takeEvery(REQUEST_TODO_LIST, sagaWorker);
}

function* sagaWorker() {
    try {
        yield put(showLoader());
        const payload = yield call(fetchTodoList);
        yield put({type: FETCH_TODO_LIST, payload});
        yield put(hideLoader());
    }catch (e) {

    }
}

const fetchTodoList = async () => {
    const response = await fetch('https://jsonplaceholder.typicode.com/todos/?_limit=9');
    const json = await response.json();
    return await json.map(todo => ({label: todo.title, important: false, done: todo.completed, id: todo.id}));
};
