import React from 'react';
import {createStore, compose, applyMiddleware} from "redux";
import createSagaMiddleware from "redux-saga";
import {Provider} from "react-redux";
import {BrowserRouter as Router} from "react-router-dom";
import thunk from "redux-thunk";
import rootReducer from "../../store/root-reducer";
import {Header} from "../header/Header";
import NavigationContainer from "../navigation/Navigation-container";
import TodoListContainer from "../todo-list/Todo-list-container";
import FormTodoContainer from "../register-form/Register-form-container";
import {sagaWatcher} from "../../store/sagas/saga-watcher";
import {Auth} from "../../pages/auth/Auht";
import './app.css';
import {useRoutes} from "../../routes";

// const store = createStore(rootReducer, applyMiddleware(thunk));

const saga = createSagaMiddleware();

const store = createStore(rootReducer, compose(
    applyMiddleware(thunk, saga),
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
);

saga.run(sagaWatcher);

function App() {
    const routes = useRoutes(false);
    return (
        <div className="App container">
            <Provider store={store}>
                {routes}
                {/*<Auth/>*/}
            </Provider>
        </div>
    );
}

export default App;
