import {USER_ID, USER_TOKEN} from "../types";

const defaultState = {
  userToken: null,
  userID: null
};

export const userReducer = (state = defaultState, action) => {
  switch (action.type) {
      case USER_TOKEN:
          return {
              ...state, userToken: action.payload
          };
      case USER_ID:
          return {
              ...state, userID: action.payload
          };
      default:
          return state
  }
};
