import React from "react";
import "./todo-item.css";

export const TodoItem = ({label, keyID, done, important, onClickDone, onClickImportant, onClickDelete}) => {
    const setDate = () => {
        const date = new Date();
        return <span className="date">
            {`(${date.getDay()}-${date.getMonth()}-${date.getFullYear()} ${date.getHours()}:${date.getMinutes()})`}
        </span>;
    };
    return (
        <li>
            <div className={done ? "todo done" : "todo"}>

                <div className="text">
                    <label>
                        <input
                            type="checkbox"
                            checked={done}
                            onChange={() => onClickDone(keyID)}
                        />
                        <span className={important ? "todo-text important" : "todo-text"}>{label}</span><br/>
                        <div>{setDate()}</div>
                    </label>
                </div>

                <div>
                    <i
                        className="material-icons Dark cyan-text"
                        onClick={() => onClickImportant(keyID)}
                    >
                        {important ? "star" : "star_border"}
                    </i>
                    <i
                        className="material-icons red-text"
                        onClick={() => onClickDelete(keyID)}
                    >
                        delete
                    </i>
                </div>
            </div>
        </li>
    )
};
