import React, {Component} from "react";
import {connect} from "react-redux";
import {Pagination} from "./Pagination";

class PaginationContainer extends Component {
    getPageNumbers = () => {
        const {totalTodoList, todoListPerPage} = this.props;
        const pageNumbers = [];

        for (let i = 1; i <= Math.ceil(totalTodoList.length / todoListPerPage); i++) {
            pageNumbers.push(i);
        }
        return pageNumbers;
    };

    handlePaginate = page => {
        console.log(page)
    };

    render() {
        return <Pagination
            pageNumbers={this.getPageNumbers()}
            paginate={this.handlePaginate}
        />;
    }
}

const mapStateToProps = state => {
    return {
        totalTodoList: state.todoList.todoList,
        todoListPerPage: state.pagination.todoListPerPage
    }
};

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(PaginationContainer);
