import {ADD_TODO, DELETE_TODO, FETCH_TODO_LIST, UPDATE_TODO_DONE, UPDATE_TODO_IMPORTANT} from "../types";

const defaultState = {
    todoList: [
        {
            label: "Drink some coffee",
            important: false,
            done: true,
            id: 1
        },
        {
            label: "Drink any milk",
            important: false,
            done: false,
            id: 2
        },
        {
            label: "Go to the cinema",
            important: false,
            done: false,
            id: 3
        }
    ]
};

export const todoListReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_TODO:
            return {
                ...state, todoList: [...state.todoList, action.payload]
            };
        case DELETE_TODO:
            console.log('was deleted');
            return {
                ...state, todoList: action.payload
            };
        case UPDATE_TODO_DONE:
            return {
                ...state, todoList: state.todoList.map(todo => {
                    if (todo.id === action.id) return {...todo, done: !todo.done};
                    return todo
                })
            };
        case UPDATE_TODO_IMPORTANT:
            return {
                ...state, todoList: state.todoList.map(todo => {
                    if (todo.id === action.id) return {...todo, important: !todo.important};
                    return todo
                })
            };
        case FETCH_TODO_LIST:
            return {
                ...state, todoList: action.payload
            };
        default:
            return state
    }
};
