import {ADD_TODO, DELETE_TODO, FETCH_TODO_LIST, UPDATE_TODO_DONE, UPDATE_TODO_IMPORTANT} from "../types";
import {hideLoader, showLoader} from "../loader/actions";

export const setNewTodo = todo => {
    return{
        type: ADD_TODO,
        payload: todo
    }
};

export const deleteTodo = todo => {
    return{
        type: DELETE_TODO,
        payload: todo
    }
};

export const updateTodoDone = (todo, id) => {
    return{
        type: UPDATE_TODO_DONE,
        payload: todo,
        id: id
    }
};

export const updateTodoImportant = (todo, id) => {
    return{
        type: UPDATE_TODO_IMPORTANT,
        payload: todo,
        id: id
    }
};

export const fetchTodoList = () => {
    return async dispatch => {
        // dispatch(showLoader(true));
        const response = await fetch('https://jsonplaceholder.typicode.com/todos/?_limit=5');
        const json = await response.json();
        const todoList = await json.map(todo => ({label: todo.title, important: false, done: todo.completed, id: todo.id}));
        setTimeout(() => dispatch({type: FETCH_TODO_LIST, payload: todoList}),2000);
        dispatch(hideLoader(true));
    }
};
