import {takeEvery, put, call} from "redux-saga/effects";
import {FETCH_TODO_LIST} from "../../store/types";
import {hideLoader, showLoader} from "../loader/actions";
import {fetchTodoList} from "../todo-list/actions";

export function* sagaWatcher() {
    yield takeEvery(FETCH_TODO_LIST, sagaWorker);
}

function* sagaWorker() {
    yield put(showLoader());
    const payload = yield call(requestTodoList);
    yield put(fetchTodoList(payload));
    yield put(hideLoader());
}

const requestTodoList = async  () => {
    const response = await fetch('https://jsonplaceholder.typicode.com/todos/?_limit=9');
    return  await response.json();
};
