import React from "react";
import {Switch, Route, Redirect} from "react-router-dom";
import {Header} from "./components/header/Header";
import NavigationContainer from "./components/navigation/Navigation-container";
import TodoListContainer from "./components/todo-list/Todo-list-container";
import FormTodoContainer from "./components/register-form/Register-form-container";
import RegisterContainer from "./pages/auth/Register-container";
import LoginContainer from "./pages/auth/Login-container";

export const useRoutes = (isAuthenticated, form) => {
    console.log(form);
    if (isAuthenticated) {
        return (
            <Switch>
                <Route path="/todo-list" exact>
                    <div>
                        <Header/>
                        <NavigationContainer/>
                        <TodoListContainer/>
                        <FormTodoContainer/>
                    </div>
                </Route>
                <Redirect to="/todo-list"/>
            </Switch>
        )
    }

    if (form === "login") {
        return (
            <Switch>

                <Route path="/login" exact>
                    <LoginContainer/>
                </Route>

                <Redirect to="/login"/>

            </Switch>
        )
    }

    if (form === "register") {
        return (
            <Switch>

                <Route path="/register" exact>
                    <RegisterContainer/>
                </Route>

                <Redirect to="/login"/>

            </Switch>
        )
    }
};
