import React, {Component} from "react";
import {connect} from "react-redux";
import axios from "axios";
import {RegisterForm} from "./Register-form";
import {setNewTodo} from "../../store/todo-list/actions";
import {setRegisterForm} from "../../store/register-form/actions";

class RegisterFormContainer extends Component {
    setDate = () => {
        const date = new Date();
        return `${date.getDay()} ${date.getMonth()} ${date.getFullYear()}`;
    };

    handleChange = event => this.props.setRegisterForm(event);

    handleSubmit = event => {
        if (event.key === 'Enter') {
            event.preventDefault();

            const newTodo = {label: `${this.props.form} | ${this.setDate()}`, important: false, done: false, key: Date.now()};
            if (this.props.form) this.props.setNewTodo(newTodo);

            this.props.setRegisterForm('');

            const userData = JSON.parse(localStorage.getItem('userData'));

            // axios.post('http://localhost:5000/todo-list/add', newTodo, { headers: {"Authorization" : `Bearer ${userData.token}`} })
            //     .then(res => console.log(res.data))
            //     .catch(err => window.M.toast({html: err.response.data.message}))
        }
    };

    render() {
        return <RegisterForm
            onChangeForm={this.handleChange}
            onSubmitForm={this.handleSubmit}
            label={this.props.form}
        />;
    }
}

const mapStateToProps = state => {
    return {
        form: state.TodoForm.label
    }
};

const mapDispatchToProps = {
    setNewTodo,
    setRegisterForm
};

export default connect(mapStateToProps, mapDispatchToProps)(RegisterFormContainer);
