import React from "react";
import "./pagination.css";

export const Pagination = ({pageNumbers, paginate, currentPage}) => {
    return (
        <div className="wrapper">
            <ul className="pagination">
                {pageNumbers.map(page =>
                    <li className={currentPage === page ? "active grey lighten-1" : "waves-effect"} key={page}>
                        <a onClick={() => paginate(page)}>{page}</a></li>)}
            </ul>


            {/*<button data-target="modal1" className="btn modal-trigger" id="btn-1">Modal</button>*/}
            <div id="modal1" className="modal modal-fixed-footer">
                <div className="modal-content">
                    <h4>Modal Header</h4>
                    <p>A bunch of text</p>
                </div>
                <div className="modal-footer">
                    <a href="#!" className="modal-action modal-close waves-effect waves-green btn-flat ">Agree</a>
                </div>

            </div>
        </div>
    )
};
