import React, {Component} from "react";
import {RegisterForm} from "./Register-form";
import {connect} from "react-redux";
import {setNewTodo} from "../../store/todo-list/actions";
import {setRegisterForm} from "../../store/register-form/actions";

class RegisterFormContainer extends Component{
    handleChange = event => {
        this.props.setRegisterForm(event);
    };

    handleSubmit = event => {
        if (event.key === 'Enter'){
            event.preventDefault();
            const newTodo = {label: this.props.label, important: false, done: false, id: Date.now()};
            this.props.setNewTodo(newTodo);
            this.props.setRegisterForm('');
        }
        // this.props.setNewTodo(this.props.label)
    };

    render() {
        return <RegisterForm
            onChangeForm={this.handleChange}
            onSubmitForm={this.handleSubmit}
            label={this.props.label}
        />;
    }
}

const mapStateToProps = state => {
    return{
        label: state.registerForm.label
    }
};

const mapDispatchToProps = {
    setNewTodo,
    setRegisterForm
};

export default connect(mapStateToProps, mapDispatchToProps)(RegisterFormContainer);
