import React, {Component} from "react";
import {connect} from "react-redux";
import {TodoItem} from "./Todo-item";
import {setNewTodo, deleteTodo, updateTodo} from "../../store/todo-list/actions";

class TodoItemContainer extends Component {
    onClickDone = id => {
        const [todo] = this.props.todoList.filter(todo => todo.id === id);
        if (todo) updateTodo({...todo, done: !todo.done});
    };

    render() {
        const {todo: {label, important, done, id}} = this.props;

        return <TodoItem
            label={label}
            id={id}
            done={done ? 'done' : ''}
            important={important}
            onClickDone={this.onClickDone}
        />;
    }
}

const mapStateToProps = state => {
    return {
        todoList: state.todoList.todoList
    }
};

const mapDispatchToProps = {
    setNewTodo,
    deleteTodo,
    updateTodo
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoItemContainer);
