import React, {Component} from "react";
import {connect} from "react-redux";
import {TodoList} from "./Todo-list";
import {fetchTodoList, setNewTodo, deleteTodo} from "../../store/todo-list/actions";

class TodoListContainer extends Component {
    componentDidMount() {
        this.props.fetchTodoList();
    }

    todoList = () => {
        const {todoList, term, filter} = this.props;
        switch (filter) {
            case "Active":
                return todoList.filter(todo => {
                    if (!todo.done && term.trim()) {
                        return todo.label.toLowerCase().indexOf(term) !== -1
                    } else
                        return !todo.done;
                });
            case "Done":
                return todoList.filter(todo => {
                    if (todo.done && term.trim()) {
                        return todo.label.toLowerCase().indexOf(term) !== -1
                    } else
                        return todo.done;
                });
            default:
                return todoList.filter(todo => {
                    if (term.trim()) {
                        return todo.label.toLowerCase().indexOf(term) !== -1
                    } else
                        return todo;
                });
        }
    };

    render() {
        return <TodoList
            todoList={this.todoList()}
            isEmpty={this.props.todoList.length}
        />
    }
}

const mapStateToProps = state => {
    return {
        todoList: state.todoList.todoList,
        filter: state.navigation.filter,
        term: state.searchForm.term
    }
};

const mapDispatchToProps = {
    setNewTodo,
    deleteTodo,
    fetchTodoList
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoListContainer);
