import {CURRENT_PAGE} from "../types";

const defaultState = {
    currentPage: 1,
    todoListPerPage: 5
};

export const paginationReducer = (state = defaultState, action) => {
    if (action.type === CURRENT_PAGE) {
        return {
            ...state, currentPage: action.payload
        }
    } else
        return state;
};
