import React, {Component} from "react";
import {connect} from "react-redux";
import {TodoList} from "./Todo-list";
import {setNewTodo, deleteTodo} from "../../store/todo-list/actions";

class TodoListContainer extends Component {
    todoList = () => {
        switch (this.props.filter) {
            case "Active":
                return this.props.todoList.filter(todo => {
                    if (!todo.done && this.props.term.length) {
                        return todo.label.toLowerCase().indexOf(this.props.term) !== -1
                    } else
                        return !todo.done;
                });
            case "Done":
                return this.props.todoList.filter(todo => {
                    if (todo.done && this.props.term.length) {
                        return todo.label.toLowerCase().indexOf(this.props.term) !== -1
                    } else
                        return todo.done;
                });
            default:
                return this.props.todoList;
        }
    };

    render() {
        return <TodoList
            todoList={this.todoList()}
        />
    }
}

const mapStateToProps = state => {
    return {
        todoList: state.todoList.todoList,
        filter: state.navigation.filter,
        term: state.searchForm.term
    }
};

const mapDispatchToProps = {
    setNewTodo,
    deleteTodo
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoListContainer);
