import React, {Component} from "react";
import {connect} from "react-redux";
import {TodoItem} from "./Todo-item";
import {setNewTodo, deleteTodo, updateTodoDone, updateTodoImportant} from "../../store/todo-list/actions";

class TodoItemContainer extends Component {
    handleDone = id => {
        const [todo] = this.props.todoList.filter(todo => todo.id === id);
        if (todo)this.props.updateTodoDone({...todo, done: !todo.done}, todo.id);
    };

    handleImportant = id => {
        const [todo] = this.props.todoList.filter(todo => todo.id === id);
        if (todo)this.props.updateTodoImportant({...todo, important: !todo.important}, todo.id);
    };

    handleDelete = id => {
        const todo = this.props.todoList.filter(todo => todo.id !== id);
        this.props.deleteTodo(todo);
    };

    render() {
        const {todo: {label, important, done, id}} = this.props;

        return <TodoItem
            label={label}
            id={id}
            done={done}
            important={important}
            onClickDone={this.handleDone}
            onClickDelete={this.handleDelete}
            onClickImportant={this.handleImportant}
        />;
    }
}

const mapStateToProps = state => {
    return {
        todoList: state.todoList.todoList,
        test: state.todoList.test
    }
};

const mapDispatchToProps = {
    setNewTodo,
    deleteTodo,
    updateTodoDone,
    updateTodoImportant
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoItemContainer);
