import React from "react";
import "./todo-item.css";

export const TodoItem = ({label, id, done, important, onClickDone, onClickImportant}) => {
    const cls = ['todo'];
    if (done) cls.push('done');
    return (
        <div className='ccc'>
            <li className={cls.join(' ')}>


                <div className="todo-item-1">
                    <input
                        type="checkbox"
                        checked={done}
                        onClick={() => onClickDone(id)}
                    />
                    <span>Red</span>
                    <div>
                        <i
                            className="material-icons"
                            onClick={() => onClickImportant(id)}
                        >
                            star_border
                        </i>
                        <i
                            className="material-icons red-text"
                            // onClick={() => dispatch({
                            //     type: 'remove',
                            //     payload: id
                            // })}
                        >
                            delete
                        </i>
                    </div>
                </div>


                <label htmlFor="check-todo" className="todo-item">
                    <input
                        id="check-todo"
                        type="checkbox"
                        checked={done}
                        onClick={() => onClickDone(id)}
                        onChange={onClickDone}
                    />
                    <span className="todo-text">{label}</span>
                    <div>
                        <i
                            className="material-icons"
                            onClick={() => onClickImportant(id)}
                        >
                            star_border
                        </i>
                        <i
                            className="material-icons red-text"
                            // onClick={() => dispatch({
                            //     type: 'remove',
                            //     payload: id
                            // })}
                        >
                            delete
                        </i>
                    </div>
                </label>
            </li>

        </div>
    )
};
