import React from "react";
import "./todo-item.css";

export const TodoItem = ({label, id, done, important, onClickDone, onClickImportant}) => {
    const cls = ['todo'];
    if (done) cls.push('done');
    return (
        <div className='ccc'>
            <li className={cls.join(' ')}>


                {/*<div className="todo-item-1">*/}
                {/*    <label>*/}
                {/*        <input*/}
                {/*            type="checkbox"*/}
                {/*            // checked={important}*/}
                {/*            onChangeCapture={() => onClickDone(id)}*/}
                {/*            onChange={onClickDone}*/}
                {/*        />*/}
                {/*        <span>Red</span>*/}
                {/*    </label>*/}
                {/*    <div>*/}
                {/*        <i*/}
                {/*            className="material-icons"*/}
                {/*            onClick={() => onClickImportant(id)}*/}
                {/*        >*/}
                {/*            star_border*/}
                {/*        </i>*/}
                {/*        <i*/}
                {/*            className="material-icons red-text"*/}
                {/*            // onClick={() => dispatch({*/}
                {/*            //     type: 'remove',*/}
                {/*            //     payload: id*/}
                {/*            // })}*/}
                {/*        >*/}
                {/*            delete*/}
                {/*        </i>*/}
                {/*    </div>*/}
                {/*</div>*/}


                <label className="todo-item">
                    <input
                        type="checkbox"
                        checked={done}
                        onClick={() => onClickDone(id)}
                        onChange={onClickDone}
                    />
                    <span className="todo-text">{label}</span>
                    <div>
                        <i
                            className="material-icons"
                            onClick={() => onClickImportant(id)}
                        >
                            star_border
                        </i>
                        <i
                            className="material-icons red-text"
                            // onClick={() => dispatch({
                            //     type: 'remove',
                            //     payload: id
                            // })}
                        >
                            delete
                        </i>
                    </div>
                </label>


                <div>
                    <p>
                        <label>
                            <input type="checkbox"/>
                            <span>1</span>
                        </label>
                        test
                    </p>
                </div>

            </li>
        </div>
    )
};
