import {SING_UP_CONFIRM_PASSWORD, SING_UP_PASSWORD, SING_UP_USERNAME} from "../types";


const defaultState = {
  username: "",
  password: "",
  confirmPassword: ""
};

export const registerReducer = (state = defaultState, action) => {
    switch (action.type) {
        case SING_UP_USERNAME:
            return {
                ...state, username: action.payload
            };
        case SING_UP_PASSWORD:
            return {
                ...state, password: action.payload
            };
        case SING_UP_CONFIRM_PASSWORD:
            return {
                ...state, confirmPassword: action.payload
            };
        default:
            return state;
    }
};
