import React from "react";
import "./todo-item.css";

export const TodoItem = ({label, id, done, important, onClickDone, onClickImportant, onClickDelete}) => {

    return (
        <li>

            <div className={done ? "todo done" : "todo"}>

                <div className="text">
                    <label className={important ? "todo-item important" : "todo-item"}>
                        <input
                            type="checkbox"
                            checked={done}
                            onClick={() => onClickDone(id)}
                            onChange={onClickDone}
                        />
                        <span className="todo-text">{label}</span>
                    </label>
                </div>

                <div>
                    <i
                        className="material-icons Dark cyan-text"
                        onClick={() => onClickImportant(id)}
                    >
                        {important ? "star" : "star_border"}
                    </i>
                    <i
                        className="material-icons red-text"
                        onClick={() => onClickDelete(id)}
                    >
                        delete
                    </i>
                </div>
            </div>
        </li>
    )
};
